        }

        private void btnAgregar_Click( object sender, EventArgs e )
        {
            FormAgregar1 formAgregar1 = new FormAgregar1();

            if ( formAgregar1.ShowDialog() == DialogResult.OK )
            {
                RegistroMantenimiento nuevoRegistro = formAgregar1.NuevoRegistro;

                if ( registros.Any( r => r.ID == nuevoRegistro.ID ) )
                {
                    registros.RemoveAll( r => r.ID == nuevoRegistro.ID );
                }
                registros.Add( nuevoRegistro );

                MessageBox.Show( nuevoRegistro.ToString() , "Éxito", MessageBoxButtons.OK, MessageBoxIcon.Information );

                FiltrarPorSemana( DateTime.Parse( nuevoRegistro.Fecha ) );
            }
        }

        private void btnEditar_Click( object sender, EventArgs e )
        {
            if ( dataGridView1.SelectedRows.Count == 0 )
            {
                MessageBox.Show( "Seleccione una fila para editar.", "Aviso", MessageBoxButtons.OK, MessageBoxIcon.Information );
                return;
            }
            DataGridViewRow filaSeleccionada = dataGridView1.SelectedRows[0];
            string idSeleccionado = filaSeleccionada.Cells[0].Value.ToString();
            RegistroMantenimiento registroAEditar = registros.FirstOrDefault( r => r.ID == idSeleccionado );
            if ( registroAEditar != null )
            {
                FormAgregar1 formEditar1 = new FormAgregar1( registroAEditar );

                if ( formEditar1.ShowDialog() == DialogResult.OK )
                {
                    RegistroMantenimiento nuevoRegistro = formEditar1.NuevoRegistro;

                    if ( registros.Any( r => r.ID == nuevoRegistro.ID ) )
                    {
                        registros.RemoveAll( r => r.ID == nuevoRegistro.ID );
                    }
                    registros.Add( nuevoRegistro );

                    FiltrarPorSemana( dateTimePicker1.Value );
                }
            }
        }

        private void btnEliminar_Click( object sender, EventArgs e )
        {
            if ( dataGridView1.SelectedRows.Count == 0 )
            {
                MessageBox.Show( "Seleccione una fila para eliminar.", "Aviso", MessageBoxButtons.OK, MessageBoxIcon.Information );
                return;
            }
            DataGridViewRow filaSeleccionada = dataGridView1.SelectedRows[0];
            string idSeleccionado = filaSeleccionada.Cells[0].Value.ToString();
            var resultado = MessageBox.Show( "¿Está seguro de que desea eliminar el registro seleccionado?", "Confirmar eliminación", MessageBoxButtons.YesNo, MessageBoxIcon.Warning );
            if ( resultado == DialogResult.Yes )
            {
                RegistroMantenimiento registroAEliminar = registros.FirstOrDefault( r => r.ID == idSeleccionado );
                if ( registroAEliminar != null )
                {
                    registros.Remove( registroAEliminar );
                    MessageBox.Show( "Registro eliminado exitosamente.", "Éxito", MessageBoxButtons.OK, MessageBoxIcon.Information );
                    FiltrarPorSemana( dateTimePicker1.Value );
                }
            }
        }
